INSERT INTO `news` (`id`, `title`, `slug`, `excerpt`, `content`, `category`, `image_path`, `is_published`, `is_featured`, `is_urgent`, `view_count`, `tags`, `published_at`, `created_by`, `updated_by`, `created_at`, `updated_at`, `deleted_at`, `deleted_by`) VALUES
(1, 'Online Facility Reservation System Now Available for All Residents', 'online-facility-reservation-system-launch', 'The city government is pleased to announce the official launch of the Online Public Facility Reservation System, allowing residents to conveniently book venues through a digital platform.', '<p>The Local Government Unit is proud to introduce the Online Public Facility Reservation System, a digital platform designed to streamline the booking process for community facilities across the city.</p><h3>Key Features</h3><ul><li>Real-time facility availability and calendar view</li><li>Secure online payment through GCash, Maya, and bank transfer</li><li>Automated notifications for booking status updates</li><li>Digital payment slips and official receipts</li><li>Resident discount automatically applied for eligible users</li></ul><p>For assistance, visit the Help Center or contact the facility management office during business hours.</p>', 'general', NULL, 1, 1, 0, 120, '[\"launch\",\"online booking\",\"facilities\"]', '2026-01-09 04:03:25', 1, NULL, '2026-02-08 04:03:25', '2026-02-08 04:03:25', NULL, NULL),
(2, 'Main Conference Hall Renovation Complete - Now Accepting Reservations', 'main-conference-hall-renovation-complete', 'The recently renovated Main Conference Hall is now open for reservations with upgraded audio-visual equipment and improved seating capacity.', '<p>We are pleased to announce that the renovation of the Main Conference Hall has been completed.</p><h3>Upgrades Include</h3><ul><li>New high-definition projector and LED display panels</li><li>Upgraded wireless microphone and sound system</li><li>Improved air conditioning units for better ventilation</li><li>Expanded seating capacity from 150 to 200 persons</li><li>Newly installed Wi-Fi access for event attendees</li></ul><p>Rental rates remain unchanged during the introductory period.</p>', 'facility_update', NULL, 1, 0, 0, 85, '[\"conference hall\",\"renovation\",\"facility update\"]', '2026-01-24 04:03:25', 1, NULL, '2026-02-08 04:03:25', '2026-02-08 04:03:25', NULL, NULL),
(3, 'Updated Facility Rental Rates Effective January 2026', 'updated-facility-rental-rates-2026', 'Please be informed of the revised rental rates for public facilities, effective January 1, 2026, as approved by the City Council.', '<p>In accordance with City Ordinance No. 2025-089, the rental rates for public facilities have been revised effective January 1, 2026.</p><h3>Summary of Changes</h3><ul><li>Base rental rates adjusted by 5 to 10 percent depending on the facility</li><li>Resident discount maintained at the current rate</li><li>Senior citizen, PWD, and solo parent discounts remain in effect</li><li>Extension rates standardized across all venues</li><li>Equipment rental fees updated to reflect current market rates</li></ul><p>For questions regarding the rate adjustments, please contact the City Treasurer Office or visit the Help Center.</p>', 'policy_change', NULL, 1, 0, 0, 65, '[\"rates\",\"policy\",\"pricing\",\"update\"]', '2025-12-25 04:03:25', 1, NULL, '2026-02-08 04:03:25', '2026-02-08 04:03:25', NULL, NULL),
(4, 'Scheduled Maintenance: Sports Complex Closed February 10-14', 'sports-complex-maintenance-february-2026', 'The Sports Complex will be temporarily closed for scheduled maintenance from February 10 to 14, 2026. Existing reservations during this period will be rescheduled.', '<p>Please be advised that the Sports Complex will undergo scheduled preventive maintenance from <strong>February 10 to 14, 2026</strong>. The facility will be closed to the public during this period.</p><h3>Maintenance Activities</h3><ul><li>Floor resurfacing and repair of the indoor court</li><li>Inspection and servicing of electrical systems</li><li>Plumbing maintenance and restroom repairs</li><li>Repainting of common areas and exterior walls</li></ul><p>We apologize for any inconvenience. The facility is expected to reopen on <strong>February 15, 2026</strong>.</p>', 'maintenance', NULL, 1, 0, 1, 92, '[\"maintenance\",\"sports complex\",\"closure\",\"schedule\"]', '2026-02-03 04:03:25', 1, NULL, '2026-02-08 04:03:25', '2026-02-08 04:03:25', NULL, NULL),
(5, 'GCash and Maya Payments Now Accepted for Facility Reservations', 'gcash-maya-payments-accepted', 'In line with the city digital transformation initiative, cashless payment options via GCash and Maya are now available for facility rental payments.', '<p>The City Treasurer Office now accepts GCash and Maya as official payment channels for facility rental fees.</p><h3>How to Pay via GCash or Maya</h3><ol><li>Complete your booking and wait for staff verification.</li><li>Once verified, you will receive a payment slip with the GCash/Maya account details.</li><li>Send the exact amount indicated on your payment slip.</li><li>Use your booking reference number as the payment description.</li><li>The Treasurer Office will verify your payment within 1 to 2 business days.</li></ol><h3>Benefits of Cashless Payment</h3><ul><li>No need to visit the City Treasurer Office in person</li><li>Instant payment confirmation via transaction receipt</li><li>Secure and traceable digital transactions</li><li>Available 24/7 for your convenience</li></ul>', 'general', NULL, 1, 0, 0, 73, '[\"gcash\",\"maya\",\"payment\",\"cashless\",\"digital\"]', '2025-12-10 04:03:25', 1, NULL, '2026-02-08 04:03:25', '2026-02-08 04:03:25', NULL, NULL);
